mapper.select:
select * from t_quote where 1=1 
        and id=#{id}
        and createTime=#{createTime}
        and price=#{price}
        and area=#{area}
        and source=#{source}
        and province=#{province}
        and userName=#{userName}
        and type=#{type}
        and mobile=#{mobile}
        and productType=#{productType}
        and city=#{city}order by id desc limit #{offset},#{pageSize}

select count(*) from( select 1 from t_quote where 1=1 
        and id=#{id}
        and createTime=#{createTime}
        and price=#{price}
        and area=#{area}
        and source=#{source}
        and province=#{province}
        and userName=#{userName}
        and type=#{type}
        and mobile=#{mobile}
        and productType=#{productType}
        and city=#{city})a

select * from t_quote where 1=1 
        and id=#{id}
        and createTime=#{createTime}
        and price=#{price}
        and area=#{area}
        and source=#{source}
        and province=#{province}
        and userName=#{userName}
        and type=#{type}
        and mobile=#{mobile}
        and productType=#{productType}
        and city=#{city}order by id

mapper.delete:
delete from t_quote where 1=1 

     	 and type=#{type}


	    and createTime=#{createTime}


	    and source=#{source}

mapper.update:
update t_quote set id=#{id} 
        , id=#{id}
        , createTime=#{createTime}
        , price=#{price}
        , area=#{area}
        , source=#{source}
        , province=#{province}
        , userName=#{userName}
        , type=#{type}
        , mobile=#{mobile}
        , productType=#{productType}
        , city=#{city}where 1=1 and id=#{id}
