package net.jeeshop.services.front.attribute.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.front.attribute.bean.Attribute;import net.jeeshop.services.front.attribute.dao.AttributeDao;public class AttributeDaoImpl implements AttributeDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Attribute e) {		return dao.selectPageList("front.attribute.selectPageList",				"front.attribute.selectPageCount", e);	}	@Override	public List selectList(Attribute e) {		return dao.selectList("front.attribute.selectList", e);	}	@Override	public Attribute selectOne(Attribute e) {		return (Attribute) dao.selectOne("front.attribute.selectOne", e);	}	@Override	public int delete(Attribute e) {		return dao.delete("front.attribute.delete", e);	}	@Override	public int update(Attribute e) {		return dao.update("front.attribute.update", e);	}	public int deletes(String[] ids) {		Attribute e = new Attribute();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	@Override	public int insert(Attribute e) {		return dao.insert("front.attribute.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("front.attribute.deleteById", id);	}	@Override	public void deleteByPid(Attribute e) {		 dao.delete("front.attribute.deleteByPid", e);	}		@Override	public Attribute selectById(String id) {		return (Attribute) dao.selectOne("account.selectById", id);	}}