package net.jeeshop.services.front.catalog.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.front.catalog.bean.Catalog;import net.jeeshop.services.front.catalog.dao.CatalogDao;public class CatalogDaoImpl implements CatalogDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Catalog e) {		return dao.selectPageList("front.catalog.selectPageList",				"front.catalog.selectPageCount", e);	}	@Override	public List selectList(Catalog e) {		return dao.selectList("front.catalog.selectList", e);	}	@Override	public Catalog selectOne(Catalog e) {		return (Catalog) dao.selectOne("front.catalog.selectOne", e);	}	@Override	public int delete(Catalog e) {		return dao.delete("front.catalog.delete", e);	}	@Override	public int update(Catalog e) {		return dao.update("front.catalog.update", e);	}	public int deletes(String[] ids) {		Catalog e = new Catalog();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	@Override	public int insert(Catalog e) {		return dao.insert("front.catalog.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("front.catalog.deleteById", id);	}		@Override	public Catalog selectById(String id) {		return (Catalog) dao.selectOne("account.selectById", id);	}}