package net.jeeshop.services.front.company.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.front.company.bean.Company;import net.jeeshop.services.front.company.dao.CompanyDao;public class CompanyDaoImpl implements CompanyDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Company e) {		return dao.selectPageList("front.company.selectPageList",				"front.company.selectPageCount", e);	}	@Override	public List selectList(Company e) {		return dao.selectList("front.company.selectList", e);	}	@Override	public Company selectOne(Company e) {		return (Company) dao.selectOne("front.company.selectOne", e);	}	@Override	public int delete(Company e) {		return dao.delete("front.company.delete", e);	}	@Override	public int update(Company e) {		return dao.update("front.company.update", e);	}	public int deletes(String[] ids) {		Company e = new Company();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	@Override	public int insert(Company e) {		return dao.insert("front.company.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("front.company.deleteById", id);	}	@Override	public Company selectById(String id) {		return (Company) dao.selectOne("front.company.selectById", id);	}}