package net.jeeshop.services.manage.activity.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.activity.bean.Activity;import net.jeeshop.services.manage.activity.dao.ActivityDao;public class ActivityDaoImpl implements ActivityDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Activity e) {		return dao.selectPageList("manage.activity.selectPageList",				"manage.activity.selectPageCount", e);	}	@Override	public List selectList(Activity e) {		return dao.selectList("manage.activity.selectList", e);	}	@Override	public Activity selectOne(Activity e) {		return (Activity) dao.selectOne("manage.activity.selectOne", e);	}	@Override	public int delete(Activity e) {		return dao.delete("manage.activity.deleteById", e);	}	@Override	public int update(Activity e) {		return dao.update("manage.activity.update", e);	}	public int deletes(String[] ids) {		Activity e = new Activity();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	@Override	public int insert(Activity e) {		return dao.insert("manage.activity.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("manage.activity.deleteById", id);	}	@Override	public Activity selectById(String id) {		return (Activity) dao.selectOne("manage.activity.selectById",id);	}	@Override	public Activity selectByPid(String productID) {		return (Activity) dao.selectOne("manage.activity.selectByPid",productID);	}}