package net.jeeshop.services.manage.catalog.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.catalog.bean.Catalog;import net.jeeshop.services.manage.catalog.dao.CatalogDao;public class CatalogDaoImpl implements CatalogDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Catalog e) {		return dao.selectPageList("manage.catalog.selectPageList",				"manage.catalog.selectPageCount", e);	}	@Override	public List selectList(Catalog e) {		return dao.selectList("manage.catalog.selectList", e);	}	@Override	public Catalog selectOne(Catalog e) {		return (Catalog) dao.selectOne("manage.catalog.selectOne", e);	}	@Override	public int delete(Catalog e) {		return dao.delete("manage.catalog.delete", e);	}	@Override	public int update(Catalog e) {		return dao.update("manage.catalog.update", e);	}	public int deletes(String[] ids) {		Catalog e = new Catalog();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	@Override	public int insert(Catalog e) {		return dao.insert("manage.catalog.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("manage.catalog.deleteById", id);	}	@Override	public void deletes(List<String> ids) {		dao.delete("manage.catalog.deleteByIds", ids);			}	@Override	public Catalog selectById(String id) {		return (Catalog) dao.selectOne(id);	}}