package net.jeeshop.services.manage.company.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.company.bean.Company;import net.jeeshop.services.manage.company.dao.CompanyDao;public class CompanyDaoImpl implements CompanyDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Company e) {		return dao.selectPageList("manage.company.selectPageList",				"manage.company.selectPageCount", e);	}	@Override	public List selectList(Company e) {		return dao.selectList("manage.company.selectList", e);	}	@Override	public Company selectOne(Company e) {		return (Company) dao.selectOne("manage.company.selectOne", e);	}	@Override	public int delete(Company e) {		dao.delete("manage.company.deleteByCompID", e.getId());		return dao.delete("manage.company.delete", e);	}	@Override	public int update(Company e) {		return dao.update("manage.company.update", e);	}	public int deletes(String[] ids) {		Company e = new Company();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);			dao.delete("manage.company.deleteByCompID", ids[i]);		}		return 0;	}	@Override	public int insert(Company e) {		return dao.insert("manage.company.insert", e);	}	@Override	public int deleteById(int id) {		dao.delete("manage.company.deleteByCompID", id);		return dao.delete("manage.company.deleteById", id);	}	@Override	public Company selectById(String id) {		return (Company) dao.selectOne("manage.company.selectById", id);	}}