package net.jeeshop.services.manage.company.impl;import net.jeeshop.core.ServersManager;import net.jeeshop.services.manage.company.CompanyService;import net.jeeshop.services.manage.company.bean.Company;import net.jeeshop.services.manage.company.dao.CompanyDao;import org.apache.commons.lang.StringUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class CompanyServiceImpl extends ServersManager<Company> implements		CompanyService {	private static final Logger logger = LoggerFactory			.getLogger(CompanyServiceImpl.class);	private CompanyDao companyDao;	public CompanyDao getCompanyDao() {		return companyDao;	}	public void setCompanyDao(CompanyDao companyDao) {		this.companyDao = companyDao;	}	/**	 * 添加企业信息	 */	@Override	public int insert(Company e) {		int companyID = super.insert(e);		return companyID;	}	/**	 * 更新企业信息	 */	@Override	public int update(Company e) {		super.update(e);		return 1;	}	/**	 * 批量删除企业 包括: 1、企业表数据。 2、属性关联表数据。 3、企业图片。 这个不一定要删除，因为有可能同一张图片被多处引用了。	 * 	 * @param ids	 * @return	 */	@Override	public int deletes(String[] ids) {		if (ids != null && ids.length > 0) {			for (int i = 0; i < ids.length; i++) {				String id = ids[i];				logger.error("删除企业deleteById.id=" + id);				if (StringUtils.isBlank(id)) {					throw new NullPointerException();				}				companyDao.deleteById(Integer.parseInt(id));			}		}		return 0;	}}