package net.jeeshop.services.manage.email.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.email.bean.Email;import net.jeeshop.services.manage.email.dao.EmailDao;public class EmailDaoImpl implements EmailDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Email e) {		return dao.selectPageList("manage.email.selectPageList",				"manage.email.selectPageCount", e);	}	@Override	public List selectList(Email e) {		return dao.selectList("manage.email.selectList", e);	}	@Override	public Email selectOne(Email e) {		return (Email) dao.selectOne("manage.email.selectOne", e);	}	@Override	public int delete(Email e) {		return dao.delete("manage.email.delete", e);	}	@Override	public int update(Email e) {		return dao.update("manage.email.update", e);	}	public int deletes(String[] ids) {		Email e = new Email();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	@Override	public int insert(Email e) {		return dao.insert("manage.email.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("manage.email.deleteById", id);	}	@Override	public Email selectById(String id) {		return (Email) dao.selectOne("manage.email.selectById", id);	}}