package net.jeeshop.services.manage.profit.dao;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.common.Profit;public class ProfitDaoImpl implements ProfitDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Profit e) {		return dao.selectPageList("manage.profit.selectPageList",				"manage.profit.selectPageCount", e);	}	@Override	public List selectList(Profit e) {		return dao.selectList("manage.profit.selectList", e);	}	@Override	public Profit selectOne(Profit e) {		return (Profit) dao.selectOne("manage.profit.selectOne", e);	}	@Override	public int delete(Profit e) {		return dao.delete("manage.profit.delete", e);	}	@Override	public int update(Profit e) {		return dao.update("manage.profit.update", e);	}		@Override	public int insert(Profit e) {		return dao.insert("manage.profit.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("manage.profit.deleteById", id);	}	@Override	public Profit selectById(String id) {		return (Profit) dao.selectOne("manage.profit.selectById", id);	}	@Override	public String getProfitSum(Profit e) {		Object object = dao.selectOne("manage.profit.sumProfit",e);		if(object == null){			return "0";		}else{			return object.toString();		}	}	@Override	public PagerModel getExpenditureList(Profit e){		return dao.selectPageList("manage.profit.getExpenditureList","manage.profit.getExpenditureCount", e);	}	@Override	public PagerModel getIncomeList(Profit e){		return dao.selectPageList("manage.profit.getIncomeList","manage.profit.getIncomeCount", e);	}}