package net.jeeshop.services.manage.task.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.task.bean.Task;import net.jeeshop.services.manage.task.dao.TaskDao;public class TaskDaoImpl implements TaskDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(Task e) {		return dao.selectPageList("manage.task.selectPageList",				"manage.task.selectPageCount", e);	}	@Override	public List selectList(Task e) {		return dao.selectList("manage.task.selectList", e);	}	@Override	public Task selectOne(Task e) {		return (Task) dao.selectOne("manage.task.selectOne", e);	}	@Override	public int delete(Task e) {		return dao.delete("manage.task.delete", e);	}	@Override	public int update(Task e) {		return dao.update("manage.task.update", e);	}	public int deletes(String[] ids) {		Task e = new Task();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	@Override	public int insert(Task e) {		return dao.insert("manage.task.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("manage.task.deleteById", id);	}	@Override	public Task selectById(String id) {		return (Task) dao.selectOne("manage.task.deleteById", id);	}}