package net.jeeshop.services.manage.tuiGuang.dao;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.common.TuiGuang;public class TuiGuangDaoImpl implements TuiGuangDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	@Override	public PagerModel selectPageList(TuiGuang e) {		return dao.selectPageList("front.tuiGuang.selectPageList",				"front.tuiGuang.selectPageCount", e);	}	@Override	public List selectList(TuiGuang e) {		return dao.selectList("front.tuiGuang.selectList", e);	}	@Override	public TuiGuang selectOne(TuiGuang e) {		return (TuiGuang) dao.selectOne("front.tuiGuang.selectOne", e);	}	@Override	public int delete(TuiGuang e) {		return dao.delete("front.tuiGuang.delete", e);	}	@Override	public int update(TuiGuang e) {		return dao.update("front.tuiGuang.update", e);	}		@Override	public int insert(TuiGuang e) {		return dao.insert("front.tuiGuang.insert", e);	}	@Override	public int deleteById(int id) {		return dao.delete("front.tuiGuang.deleteById", id);	}	@Override	public TuiGuang selectById(String id) {		return (TuiGuang) dao.selectOne("front.tuiGuang.selectById", id);	}}