package net.jeeshop.web.action.manage.email;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.email.EmailService;import net.jeeshop.services.manage.email.bean.Email;public class EmailAction extends BaseAction<Email> {	private static final long serialVersionUID = 1L;	private EmailService emailService;	public EmailService getEmailService() {		return emailService;	}	@Override	protected void selectListAfter() {		pager.setPagerUrl("email!selectList.action");	}	public void setEmailService(EmailService emailService) {		this.emailService = emailService;	}	@Override	public Email getE() {		return this.e;	}	@Override	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Email();		}	}	@Override	public void insertAfter(Email e) {		e.clear();	}}