package net.jeeshop.web.action.manage.express;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.express.ExpressService;import net.jeeshop.services.manage.express.bean.Express;public class ExpressAction extends BaseAction<Express> {	private static final long serialVersionUID = 1L;	private ExpressService expressService;	public ExpressService getExpressService() {		return expressService;	}	@Override	protected void selectListAfter() {		pager.setPagerUrl("express!selectList.action");	}	public void setExpressService(ExpressService expressService) {		this.expressService = expressService;	}	@Override	public Express getE() {		return this.e;	}	@Override	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Express();		}	}	@Override	public void insertAfter(Express e) {		e.clear();	}}